def apply_patch_plan(patch_plan):
    # Reduce the number of changes per patch plan
    max_lines_per_change = 100
    for change in patch_plan['changes']:
        if len(change) > max_lines_per_change:
            raise ValueError('Exceeded max_lines_per_change.')
    # Apply the patch plan
    apply_changes(patch_plan)

